import { all, call, put, takeEvery } from 'redux-saga/effects';
import axios from 'axios'
import { notification } from 'antd';

import { actionTypes, loginSuccess, logOutSuccess } from './action';
import { API } from '../API/Api';

const modalSuccess = (type) => {
    notification[type]({
        message: 'Wellcome back',
        description: 'You are login successful!',
    });
};

const modalWarning = (type) => {
    notification[type]({
        message: 'Good bye!',
        description: 'Your account has been logged out!',
    });
};

const loginAdmin = async (loginCred)=>{
    console.log(loginCred)
    const url = 'https://zozo-auction.herokuapp.com/api/v1/login'

     const data = axios.post(url, loginCred).then((response)=>{
          return response.data
     })
     return data
}


function* loginSaga(payload) {
    try {
        console.log(payload.loginCred)
        yield call(loginAdmin, payload.loginCred);
        yield put(logInSuccess());

        modalSuccess('success');
    } catch (err) {
        console.log(err);
    }
}

function* logOutSaga() {
    try {
        yield put(logOutSuccess());
        modalWarning('warning');
    } catch (err) {
        console.log(err);
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.LOGIN_REQUEST, loginSaga)]);
    yield all([takeEvery(actionTypes.LOGOUT, logOutSaga)]);
}
