import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { actionAdminFetchPending } from "~/store/bid/action";
import BidColumn from "./table-columns/BidColumn";

function BidTable() {
	const dispatch = useDispatch();
	const { pendingBids, bidGetLoading } = useSelector((state) => state.bid);

	useEffect(() => {
		dispatch(actionAdminFetchPending());
	}, []);

    console.log( pendingBids)

	let bidEvents;
	if (!bidGetLoading) {
		bidEvents = pendingBids.map((bid, index) => {
			return <BidColumn bid={pendingBids} num={index} key={index} />;
		});
	}

	return (
		<div className="table-responsive">
			<table className="table ps-table">
				<thead>
					<tr>
						<th>ID</th>
						<th>Name</th>

						<th>Access Amount</th>
						<th>Minimum Amount</th>
						<th>Approve</th>
						<th> Created Date</th>
						{/* <th>Bid Status</th>
                          <th>Create Event</th>
                          <th></th> */}
					</tr>
				</thead>
				<tbody>{!bidGetLoading && bidEvents}</tbody>
			</table>
		</div>
	);
}

export default BidTable;
