import { all, call, put, takeEvery } from 'redux-saga/effects';
import { notification } from 'antd';

import { actionTypes, addCategory } from './action';
import axios from 'axios';
import { API } from '../API/Api';


const sagaAddCategory= (cate)=>{
      console.log(cate)

      const url = API.BASE_URL + '/category/creates'
      const config = {
            headers:{
                  'Authorization': 'Bearer' + API.TOKEN
            }
      }
      axios.post(url,cate,config).then((response)=>{
            return response.status
            console.log(response.data.message)
      }).catch((err)=>{
            console.log(err)
      })

}

function* postCategory (payload){
try{
      const isCatAdded = yield call(sagaAddCategory, payload.category)
}catch (err){
      console.log(err)
}
}

export default function* rootSaga() {
      yield all([takeEvery(actionTypes.CATEGORY_POST, postCategory)]);
      // yield all([takeEvery(actionTypes.LOGOUT, logOutSaga)]);
  }
