import { useRouter } from "next/router";
import React, { useEffect, useState } from "react";
import { API, whoami } from "~/store/API/Api";
import axios from "axios"

function protectedRoute(WrappedComponent) {
	return (props) => {
		const router = useRouter();
            
		const [isTokenVerified, setIsTokenVerified] = useState(false);
		const accessToken = API.TOKEN;
       

            useEffect(()=>{

                 const whoami = async () => {
                        const url = API.BASE_URL+"/whoami";
                        const config = {
                              headers: {
                                    Authorization: "Bearer " + API.TOKEN,
                              },
                        };
                        const data = await axios.get(url, config).then((res) => {
                              setIsTokenVerified(true)
                              return res.data;
                        }).catch((err)=>{
                              setIsTokenVerified(true)
                        });
                  
                        return data;
                  };

                   whoami(accessToken)

                  // setTimeout(()=>{

                  //       // if no accessToken was found,then we redirect to "/" page.
                  //              if (!accessToken) {
                  //                    router.replace("/merchant/login");
                  //                    console.log(accessToken)
                  //              }else{
                  //                    const verify =  whoami(accessToken)
                                     
                  //                   if(!verify){
                  //                         localStorage.removeItem("accessToken");
                  //                         router.replace("/merchant/login");
                  //                   }else{
                  //                         setIsTokenVerified(true)
                  //                   }
                  //              }
                  // }, 2000)
            },[])

            if(isTokenVerified){
                  console.log(isTokenVerified)
                  return <WrappedComponent {...props}/>
            }else{
                 return null
            }

	};
}

export default protectedRoute;
