						<div className="form-group">
							<label>Name</label>
							<p
								style={{
									border: "none",
									borderBottom: "1px solid #DDDDDD",
									padding: "5px 20px",
								}}
							>
								{`${details.first_name} ${details.last_name}`}
							</p>
						</div>

						<div className="form-group">
							<label>Email</label>
							<p
								style={{
									border: "none",
									borderBottom: "1px solid #DDDDDD",
									padding: "5px 20px",
								}}
							>
								{details.email}
							</p>
						</div>

						<div className="form-group">
							<label>Phone Number</label>
							<p
								style={{
									border: "none",
									borderBottom: "1px solid #DDDDDD",
									padding: "5px 20px",
									display: "flex",
									justifyContent: "space-between",
								}}
