						{/* <button className="ps-btn ps-btn--gray">Cancel</button> */}
						<button className="ps-btn">Submit</button>
					</div>
				</form>
			</section>
		</ContainerDefault>
	);
};
export default connect((state) => state.app)(ViewUser);
