import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { actionAdminFetchAuctions } from "~/store/bid/action";
import BidColumn from "./table-columns/BidColumn";

function BidTable({type}) {
	const dispatch = useDispatch();
	const { bids, bidGetLoading } = useSelector((state) => state.bid);

	
	useEffect(() => {
		switch(type){
			case "pending":
			dispatch(actionAdminFetchAuctions("pending"));
			case "approved":
				dispatch(actionAdminFetchAuctions("pending"));
		}
	}, []);

	console.log(bids);

	let bidEvents;
	if (!bidGetLoading) {
		bidEvents = bids.map((bidEvent, index) => {
			return <BidColumn bidEvent={bidEvent} num={index} key={index} />;
		});
	}

	return (
		<div className="table-responsive">
			<table className="table ps-table">
				<thead>
					<tr>
						<th>ID</th>
						<th>Name</th>
						<th>Start Time</th>
						<th>Access Amount</th>
						<th>Minimum Amount</th>

						<th> Created Date</th>
						<th>Approve</th>
						<th>Decline</th>
					</tr>
				</thead>
				<tbody>{!bidGetLoading && bidEvents}</tbody>
			</table>
		</div>
	);
}

export default BidTable;
