import React from "react";
import Link from "next/link";
import { useRouter } from "next/router";

const MenuSidebar = () => {
	const router = useRouter();
	const menuItems = [
		{
			text: "Dashboard",
			url: "/",
			icon: "icon-home",
		},
		{
			text: "Pending Bids",
			url: "/bids/pending",
			icon: "icon-cog",
		},
		{
			text: "Approved Bids",
			url: "/bids/pending",
			icon: "icon-cog",
		},
		{
			text: "Completed Bids",
			url: "/settings",
			icon: "icon-cog",
		},
		{
			text: "Products",
			url: "/products",
			icon: "icon-database",
		},
		{
			text: "Orders",
			url: "/orders",
			icon: "icon-bag2",
		},
		{
			text: "Customers",
			url: "/customers",
			icon: "icon-users2",
		},
		{
			text: "Categories",
			url: "/categories",
			icon: "icon-users2",
		},
		{
			text: "Settings",
			url: "/settings",
			icon: "icon-cog",
		},
	];

	return (
		<ul className="menu">
			{menuItems.map((item, index) => (
				<li
					key={index}
					className={router.pathname === item.url ? "active" : ""}
				>
					<Link href={item.url}>
						<a>
							<i className={item.icon}></i>
							{item.text}
						</a>
					</Link>
				</li>
			))}
		</ul>
	);
};

export default MenuSidebar;
