import { all, call, put, takeEvery } from "redux-saga/effects";
import { notification } from "antd";

import { actionTypes, addCategory, addCategorySuccess } from "./action";
import axios from "axios";
import { API } from "../API/Api";

const sagaAddCategory = (cate) => {
	console.log(cate);

	const url = API.ADMIN_BASE_URL + "/category/create";
	const config = {
		headers: {
			Authorization: "Bearer" + API.TOKEN,
		},
	};
	axios
		.post(url, cate, config)
		.then((response) => {
			return response.data;
		})
		.catch((err) => {
			console.log(err);
		});
};

function* postCategory(payload) {
	try {
		const isCateAdded = yield call(sagaAddCategory, payload.category);
            yield put(addCategorySuccess(isCateAdded))
	} catch (err) {
		console.log(err);
	}
}

function* getCategory(){
      try{

      }catch (err){
            console.log(err)
      }
}

export default function* rootSaga() {
	yield all([takeEvery(actionTypes.CATEGORY_POST, postCategory)]);
	// yield all([takeEvery(actionTypes.LOGOUT, logOutSaga)]);
}
