import React, { useEffect, useState } from "react";
import ContainerDefault from "~/components/layouts/ContainerDefault";
import HeaderDashboard from "~/components/shared/headers/HeaderDashboard";
import { connect, useDispatch } from "react-redux";
import { toggleDrawerMenu } from "~/store/app/action";
import { addProduct, getProductDetails } from "~/store/product/action";
import { useSelector } from "react-redux";
import { fetchCategory } from "~/store/category/action";
import { useRouter } from "next/router";

const ViewUser = () => {
	const dispatch = useDispatch();
	const router = useRouter();

	const id = router.query.product;

	const { singleProduct, getSingleProductLoading } = useSelector(
		(state) => state.product
	);



	let details, content, isLoading;

	useEffect(() => {
		dispatch(getProductDetails(id));
		dispatch(toggleDrawerMenu(false));
		dispatch(fetchCategory());
	}, [id]);


	if (!getSingleProductLoading) {
		console.log(singleProduct.name);
		content = (
			<div className="col-xl-6 col-lg-6 col-md-12 col-sm-12 col-12">
				<figure className="ps-block--form-box">
					<figcaption style={{ color: "white" }}>General</figcaption>
					<div className="ps-block__content">
						<div className="form-group">
							<label>
								Product Name<sup>*</sup>
							</label>
							<input
								className="form-control"
								type="text"
								placeholder="Enter product name..."
								name="product"
								defaultValue={singleProduct.name}
								// defaultValue={singleProduct.name}
							/>
						</div>

						<div className="form-group">
							<label>
								Product Description<sup>*</sup>
							</label>
							<textarea
								className="form-control"
								rows="6"
								name="description"
								defaultValue={singleProduct.description}
							></textarea>
						</div>

						{/* <div className="form-group">
							<label>
								Category<sup>*</sup>
							</label>
							<select
								className="form-control"
								name="category"
								multiple
								onChange={categoryArray}
							>
								{!cateGetLoading && categories}
							</select>
						</div> */}

						<div className="form-group">
							<label>
								Price<sup>*</sup>
							</label>
							<input
								className="form-control"
								type="number"
								placeholder="Price of Product"
								name="price"
								defaultValue={singleProduct.price}
							/>
						</div>

						<div>
							<label>
								Width<sup>*</sup>
							</label>
							<input
								className="form-control"
								type="text"
								placeholder="Width"
								name="width"
								defaultValue={singleProduct.width}
							/>
						</div>

						<div className="form-group">
							<label>
								Height<sup>*</sup>
							</label>
							<input
								className="form-control"
								type="text"
								placeholder="Height"
								name="height"
								defaultValue={singleProduct.height}
							/>
						</div>

						<div className="form-group">
							<label>
								Color<sup>*</sup>
							</label>
							<input
								className="form-control"
								type="text"
								placeholder="Color"
								name="color"
								defaultValue={singleProduct.color}
							/>
						</div>

						<div className="form-group">
							<label>
								Weight<sup>*</sup>
							</label>
							<input
								className="form-control"
								type="text"
								placeholder="Weight"
								name="weight"
								defaultValue={singleProduct.n}
							/>
						</div>
					</div>
				</figure>
			</div>
		);
	}
	// if (!singleCustomerGetLoading || !singleMerchantGetLoading) {
	// 	if (type === "customer") {
	// 		details = customerDetails.account;
	// 	} else if (type === "merchant") {
	// 		details = merchantDetails.account;
